import 'package:carousel_slider/carousel_slider.dart';
import 'package:carousel_slider/carousel_controller.dart';
import 'package:flutter/material.dart';

class Slider_Indicator extends StatefulWidget {
  const Slider_Indicator({super.key});

  @override
  State<Slider_Indicator> createState() => _Slider_IndicatorState();
}

class _Slider_IndicatorState extends State<Slider_Indicator> {
  int _currentIndex=0;
List<String> imageList=[
    "data:image/png;base64,",
  "data:image/jpeg;base64,",
  "https://www.gujarattourism.com/content/gujrattourism/en/about-gujarat/_jcr_content/root/responsivegrid/pagebanner.coreimg.jpeg/1643369253714/about1.jpeg",
  "https://www.swantour.com/blogs/wp-content/uploads/2018/04/Festivals-of-Gujarat.jpg",
  "https://i.ytimg.com/vi/CgYtlzJp7x8/maxresdefault.jpg"
];
  final CarouselController _controller = CarouselController();
  @override
  Widget build(BuildContext context) {
    return Column(
      children: [
        SizedBox(height: 5,),
        Text("Gujarat Tourisum",style: TextStyle(color: Colors.black,fontSize: 20),),
        SizedBox(height: 10,),
        CarouselSlider(
          options: CarouselOptions(
            height: 200,
            enlargeCenterPage: true,
            autoPlay: true,
            aspectRatio: 16/9,
            autoPlayAnimationDuration: Duration(milliseconds: 800),
            autoPlayCurve: Curves.fastOutSlowIn,
            enableInfiniteScroll: true,
            viewportFraction: 0.8,
            onPageChanged: (index,reson){
              setState(() {
                _currentIndex=index;
              });
            }
          ),
          items: imageList.map((String imageUrl) {
            return Builder(
                builder: (BuildContext context){
                  return Container(
                    width: MediaQuery.of(context).size.width,
                    margin: EdgeInsets.symmetric(horizontal: 5.0),
                    decoration: BoxDecoration(
                      color: Color.fromARGB(255, 166, 166, 166),
                      borderRadius: BorderRadius.circular(10),
                      image: DecorationImage(
                        image: NetworkImage(imageUrl),
                        fit: BoxFit.cover,
                      )
                    ),
                  );
                });
          }).toList() ,
        ),
        SizedBox(
          height:10,
        ),
        Row(
          mainAxisAlignment: MainAxisAlignment.center,
          children: imageList.map((String url) {
            int index = imageList.indexOf(url);
            return Container(
              width: 8.0,
              height: 8.0,
              margin: EdgeInsets.symmetric(horizontal: 4.0),
              decoration: BoxDecoration(
                shape: BoxShape.circle,
                color: _currentIndex == index ? Colors.blueAccent : Colors.grey,
              ),
            );
          }).toList(),
        )
      ],
    );
  }
}
